
public class Calculadora {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     double n1 = 5;
     double n2 = 2;
     double resulS;
     double resulSU;
     double resulM;
     double resulD;
     
     resulS = n1 + n2;
     System.out.println("O resultado da soma é de " + resulS);
     resulSU = n1 - n2;
     System.out.println("O resultado da subtração é de " + resulSU);
     resulM = n1 * n2;
     System.out.println("O resultado da multiplicação é de " + resulM);
     resulD = n1 / n2;
     System.out.println("O resutlado da divisão é de " + resulD);
	}

}
