
public class Calculadora {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//cria variaveis do tipo double
		
     double n1 = 5; 
     double n2 = 2;
     double resulS;
     double resulSU;
     double resulM;
     double resulD;
     
     
     resulS = n1 + n2; //soma n1 + n2 
     System.out.println("O resultado da soma é de " + resulS); // imprime o resultado no console
     resulSU = n1 - n2;//subtrai n1 - n2
     System.out.println("O resultado da subtração é de " + resulSU);// imprime o resultado no console
     resulM = n1 * n2;//multiplica n1 * n2
     System.out.println("O resultado da multiplicação é de " + resulM);// imprime o resultado no console
     resulD = n1 / n2;//divide n1 por n2
     System.out.println("O resutlado da divisão é de " + resulD);// imprime o resultado no console
	}

}
